components/gordonbyte/common/brands/bran:
{{#if theme_settings.gb-brands-image-one }}
<div class="brands-section bg-img product-sections brandcount-{{length (split theme_settings.gb-brands-image-one  '|')}}" {{#if theme_settings.gb_default_background }}style="background-image:url({{cdn 'img/bg-img.jpg'}});"{{/if}}>
  <div class="container">

    {{#if theme_settings.gb-brands-main-title }}
      <h2 class="page-heading"><span>{{ theme_settings.gb-brands-main-title }}</span></h2>
    {{/if}}

    {{{region name="home_brands_description"}}}

    <div class="brands-selections brands-row1">
      <ul>
        {{#each (split theme_settings.gb-brands-image-one  "|") }}
            <li>
              <a>
                <div class="caticon">
                  <img src="{{getImageManagerImage this }}"
                        data-sizes="auto"
                        alt="{{this}}"
                        {{#unless lazyload '==' 'disabled'}}
                            {{#if lazyload '==' 'lazyload+lqip'}}
                            {{!-- If we want a LQIP, include it as the default srcset (will be overwritten by lazysizes) --}}
                            srcset="{{getImageManagerImageSrcset this }}"
                            {{else}}
                              {{!-- If we don't want a LQIP, use a 1px transparent gif for the stand-in image
                                    This requires no additional network request, so it's fast --}}
                            srcset="data:image/gif;base64,R0lGODlhAQABAIAAAP///wAAACH5BAEAAAAALAAAAAABAAEAAAICRAEAOw=="
                            {{/if}}
                        {{/unless}}

                        {{#unless lazyload '==' 'disabled'}}data-{{/unless}}srcset="{{getImageManagerImageSrcset this use_default_sizes=true }}"

                        class="{{#unless lazyload '==' 'disabled'}}lazyload{{/unless}}"
                        {{#if lazyload '==' 'lazyload'}}loading="lazy"{{/if}}
                        >
                </div>
              </a>
            </li>
        {{/each}}
      </ul>
    </div><!--.category-selections ends-->

  </div>
</div>
{{else}}

  {{#if theme_settings.gb-theme-demo }}
  <div class="brands-section bg-img product-sections brandcount-8 demo" {{#if theme_settings.gb_default_background }}style="background-image:url({{cdn 'img/bg-img.jpg'}});"{{/if}}>
    <div class="container">

      {{#if theme_settings.gb-brands-main-title }}
        <h2 class="page-heading"><span>{{ theme_settings.gb-brands-main-title }}</span></h2>
      {{/if}}

      {{{region name="home_brands_description"}}}

      <div class="brands-selections brands-row1">
        <ul>
          {{#for 1 8}}
              <li>
                <a>
                  <div class="caticon">
                    {{> components/gordonbyte/demo/common/img path='universal' file_name=(concat $index 'brand.png') }}
                  </div>
                </a>
              </li>
          {{/for}}
        </ul>
      </div><!--.category-selections ends-->

    </div>
  </div>
  {{/if}}
{{/if}}
